function getFormattedProducts(t){let s="";return t.forEach(t=>{s+=`
        <div class="col mb-5">
            <div class="card h-100">
                <!-- Product image-->
                <img class="card-img-top"
                    src="${t.images[0]}"
                    alt="...">
                <!-- Product details-->
                <div class="card-body p-4">
                    <div class="text-center">
                        <!-- Product name-->
                        <h6 class="fw-bolder">${t.brand}</h6>
                        <h5 class="fw-bolder">${t.title}</h5>
                        <!-- Product price-->
                        ${t.price}
                    </div>
                </div>
                <!-- Product actions-->
                <div class="card-footer p-4 pt-0 border-top-0 bg-transparent">
                    <div class="text-center">
                        <a class="btn btn-outline-dark mt-auto" href="single-product.html">View Product</a>
                    </div>
                </div>
            </div>
        </div>
        `}),s}function getFormattedPosts(t){let s="";return t.forEach(t=>{s+=`
        <div class="col-lg-6">
            <div class="card mb-4">
                <a href="#!"><img class="card-img-top"
                        src="https://dummyimage.com/700x350/dee2e6/6c757d.jpg"
                        alt="..."></a>
                <div class="card-body">
                    <div class="small text-muted">${t.tags[0]}</div>
                    <h2 class="card-title h4">${t.title}</h2>
                    <p class="card-text">${t.body}</p>
                    <a class="btn btn-primary" href="single-post.html">Read more â†’</a>
                </div>
            </div>
        </div>
        `}),s}function displayProducts(){fetch("https://dummyjson.com/products",{method:"GET"}).then(t=>t.json()).then(t=>{productsDiv=getFormattedProducts(t.products),document.getElementById("products_list").innerHTML=productsDiv}).catch(t=>console.log(t))}function displayPosts(){fetch("https://dummyjson.com/posts",{method:"GET"}).then(t=>t.json()).then(t=>{postsDiv=getFormattedPosts(t.posts),document.getElementById("posts_list").innerHTML=postsDiv}).catch(t=>console.log(t))}displayProducts(),displayPosts();